settings.css_section:
.section_id .slideshow__title {
color: #333;
font-size: 1em;
margin-bottom: .5rem;
}
.section_id .slideshow__subtitle{
color: #333;
font-size: 1.875em;
margin-bottom: .7rem;
line-height: 1.2;
}
.section_id .slideshow__description{
color: #333;
font-size: 1em;
margin-bottom: 1.2rem;
}
.section_id .btn{
padding: 0.625em 1.188em;
}
@media(max-width: 576px){
.kt_home_slide .imgRatio__text-wrap {
    font-size: .75rem;
}
}

@media (min-width: 768px) {
  .content_{{section.id}} .icon-boxes-container {
    overflow: hidden;
    box-shadow: 0 5px 30px rgba(0, 0, 0, 0.07);
  }
  .content_{{section.id}} .item-wrap {
    display: flex;
    justify-content: center;
  }
  .content_{{section.id}} .icon-box-side {
    width: auto;
  }
  .content_{{section.id}} .swiper-slide:not(:last-child) .icon-box::after {
    content: '';
    height: 37px;
    width: 1px;
    background: #e1e1e1;
    position: absolute;
    right: 0;
    top: 50%;
    transform: translateY(-50%);
  }
}
